            method: "DELETE",
    })
    .then((r) => r.json())
    .then(console.log({id}))
    .then(() => {onDeleteRoom(indRoom);
    });
}

return (
    <li>
        <h4>{room}</h4>
        <button onClick={handleDeleteClick}>Delete Room</button>
    </li>
);
}

export default RoomItem;
