import './App.css';
import React, {useEffect, useState} from 'react';
import RoomContainer from './components/RoomContainer';
import CategoryContainer from './components/CategoryContainer';

function App() {
  // const [showForm, setShowForm] = useState(false);
  const [rooms,setRooms,categories,setCategories] = useState([]);

  useEffect(() => {
    fetch('https://radiant-waters-01618.herokuapp.com/rooms')
    .then((r) => r.json())
    .then(setRooms);
    fetch('https://radiant-waters-01618.herokuapp.com/categories')
    .then((r) => r.json())
    .then(setCategories);
  },[]);

//  function handleClick() {
//    setShowForm((showForm) => !showForm);
//  }

  function handleDeleteRoom(roomToDelete) {
    const updatedRooms = rooms.filter((room) => room.id !== roomToDelete.id);
    setRooms(updatedRooms);
  }

  function handleDeleteCategory(categoryToDelete) {
    const updatedCategories = categories.filter((category) => category.id !== categoryToDelete.id);
    setCategories(updatedCategories);
  }

  return (
    <div className="App">
      <main>
        <RoomContainer 
        rooms={rooms}
        onDeleteRoom={handleDeleteRoom}
        />
        <CategoryContainer
        categories={categories}
        onDeleteCategory={handleDeleteCategory}
        />
      </main>
    </div>
  );
}

export default App;
